    df_stop_times = df_stop_times[~df_stop_times['stop_id'].str.contains('Car', case=False)] # suppression car
    df_stop_times = df_stop_times[~df_stop_times['stop_id'].str.contains('Navette', case=False)] # suppresion navette
       

    df_nombre_trains_total = pd.DataFrame()  # DataFrame pour stocker les r√©sultats de toutes les gares
    Noms_gares=liste_gares
    for Nom_gare in Noms_gares:
        code_uic = str(get_code_uic(Nom_gare))
        df_exemple = df_stop_times[df_stop_times["code_uic"] == code_uic].sort_values(by="arrival_time")
        df_exemple = df_exemple[["trip_id", "arrival_time", "departure_time", "code_uic", "stop_id"]]
    
        resultat_jointure = pd.merge(df_exemple, df_trips, on='trip_id', how='left')
        resultat_jointure = resultat_jointure[resultat_jointure['service_id'].isin(liste_service)]
        resultat_jointure = resultat_jointure[["trip_id", "arrival_time", "departure_time", "code_uic", "route_id", "direction_id", "stop_id"]]
        resultat_jointure = pd.merge(resultat_jointure, df_routes, on='route_id', how='left')
        resultat_jointure = resultat_jointure[["route_short_name", "arrival_time", "departure_time", "route_long_name", "direction_id", 'trip_id', "stop_id"]]
        resultat_jointure['numero_train'] = resultat_jointure['trip_id'].apply(extraire_six_chiffres)
        resultat_jointure = resultat_jointure.dropna(subset=['direction_id'])
        resultat_jointure = resultat_jointure[["numero_train", "route_short_name", "arrival_time", "departure_time", "route_long_name"]]
        resultat_jointure['nom_gare'] = Nom_gare  # Ajouter le nom de la gare au DataFrame
    
        df_nombre_trains_total = pd.concat([df_nombre_trains_total, resultat_jointure], ignore_index=True)

    df_nombre_trains_total = df_nombre_trains_total.drop_duplicates().reset_index(drop=True)

    # Pour calculer et afficher le nombre de trains pour chaque gare :
    compteur_trains=0
    for Nom_gare in Noms_gares:
        df_temp = df_nombre_trains_total[df_nombre_trains_total['nom_gare'] == Nom_gare]
        nombre_trains = len(df_temp['numero_train'].unique())
        texte = str(nombre_trains) + " trains TER passent en gare de " + Nom_gare + " (estimation GTFS " + date_du_jour2() + ")."
        compteur_trains+=nombre_trains
        #st.write(texte)
    nombre_trains_programmes = df_nombre_trains_total['numero_train'].nunique()
    texte ="Au total, le " + date_du_jour2() + " il y a "+str(compteur_trains)+" passages de trains TER programm√©s dans les gares de la R√©gion des Hauts-de-France."+ str(nombre_trains_programmes) + " trains TER sont programm√©s aujourd'hui."
    st.write(texte)
    
    # Affichage global des donn√©es
    #st.dataframe(df_nombre_trains_total)

    aggregation_df = df_nombre_trains_total.groupby('nom_gare').agg(
    nombre_passages_TERs_HdF=pd.NamedAgg(column='nom_gare', aggfunc='size'),
    liste_departure_time=pd.NamedAgg(column='departure_time', aggfunc=lambda x: list(x))).reset_index()
    aggregation_df=aggregation_df.sort_values(by='nombre_passages_TERs_HdF', ascending=False).reset_index( drop=True)
    st.dataframe(aggregation_df)
    

        

    st.download_button(
        label="Exporter en CSV",
        data=convert_df(aggregation_df),
        file_name=date_du_jour2().replace("-", "_")+'_Total_passages_trains'+'.csv',
        mime='text/csv',
    )
    st.markdown("**_Source_** : SNCF Voyageurs (Open data SNCF)")
    


